import React from 'react';
import styles from './ExclamationError.module.css';

class ExclamationError extends React.Component {
  constructor() {
    super();
    this.state = {
      isVisible: false
    };
  }

  componentDidMount() {
    console.log('Mounted');
    this.setState({
      isVisible: true
    });
  }

  componentDidUpdate() {
    console.log('Updated');
  }

  render() {
    console.log('called');
    const { isVisible } = this.state;

    let exclamationErrorContainerClassNames = '';

    if (isVisible) {
      exclamationErrorContainerClassNames = `${styles.exclamationErrorContainer} ${styles.visible}`;
    } else {
      exclamationErrorContainerClassNames = `${styles.exclamationErrorContainer}`;
    }

    return (
      <div className={exclamationErrorContainerClassNames}>
        <span className={styles.exclamationError}>!</span>
      </div>
    );
  }
}

export default ExclamationError;
