import React from 'react';
import styles from './AmountToPayTextField.module.css';

class AmountToPayTextField extends React.Component {
  constructor() {
    super();

    this.state = {
      isFocused: false
    };
  }

  render() {
    const { isFocused } = this.state;

    return (
      <div className={isFocused ? `${styles.amountToPayTextFieldContainer} ${styles.focused}` : `${styles.amountToPayTextFieldContainer}`}>
        <input
          type="text"
          className={styles.amountToPayTextField}
          placeolhder="$0.00"
        />
      </div>
    );
  }
}

export default AmountToPayTextField;
